mapper.select:
select * from (select a.*, rowNum rn from (
	select * from sms_sellboard where mcate_no=#{mcate_no}		
	order by sb_no desc) a) 
		where rn between #{startRow} and #{endRow}

select * from (select a.*, rowNum rn from (
	select * from sms_sellboard where scate_no=#{scate_no} 		
	order by sb_no desc) a) 
		where rn between #{startRow} and #{endRow}
